// Login.js
import React, { useState } from 'react';
import './Login.css';
import { useHistory } from 'react-router-dom';
import FacebookLoginComponent from './FacebookLogin'; // Import the Facebook login component
import GmailLoginComponent from './GmailLoginComponent';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loggedInWithFacebook, setLoggedInWithFacebook] = useState(false); // State to track if logged in with Facebook
  const [loggedInWithGmail, setLoggedInWithGmail] = useState(false); // State to track if logged in with Gmail
  const history = useHistory();

  const handleSubmit = (e) => {
    e.preventDefault();
    // Your logic to handle form submission goes here
    // ...

    // Redirect to home page after login
    history.push('/');
  };

  return (
    <form className="form" onSubmit={handleSubmit}>
      <span className="login-title">Login Form</span>
      <div className="form-group">
        <label htmlFor="email">Email:</label>
        <input
          className="form-control"
          type="email"
          id="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
      </div>
      <div className="form-group">
        <label htmlFor="password">Password:</label>
        <input
          className="form-control"
          type="password"
          id="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
      </div>
      <button className="submit-button" type="submit">
        Sign In
      </button>

      <GmailLoginComponent setLoggedInWithFacebook={setLoggedInWithGmail} />

{loggedInWithGmail && (
  <div>
    {/* Display user account information when logged in with Facebook */}
    <p>You are logged in with Facebook.</p>
  </div>
)}

      <FacebookLoginComponent setLoggedInWithFacebook={setLoggedInWithFacebook} />

      {loggedInWithFacebook && (
        <div>
          {/* Display user account information when logged in with Facebook */}
          <p>You are logged in with Facebook.</p>
        </div>
      )}

      <div className="register-link">
        <span>Don't have an account? </span>
        <button className="register-button" onClick={handleRegisterClick}>
          Register
        </button>
      </div>
    </form>
  );
}

export default Login;
