import React, { useState } from 'react';


const RegistrationForm = () => {
  const [formData, setFormData] = useState({
    email: '',
    username: '',
    password: '',
    confirmPassword: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const sendOtpViaEmail = async (email, otp) => {
    console.log('Sending OTP via email:', otp);

    const response = await fetch('your_backend_url/send-otp', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ email, otp }),
    });
  
    if (response.ok) {
      console.log('OTP sent successfully to email.');
    } else {
      console.error('Failed to send OTP via email.');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Generate a random OTP and include it in the form data
    const otp = Math.floor(100000 + Math.random() * 900000);
    setFormData({ ...formData, otp });

    // Log the generated OTP
    console.log('Generated OTP:', otp);

    await sendOtpViaEmail(formData.email, otp);


    console.log('OTP sent successfully. Redirect to verify OTP page.');

// Đây là code để khi nhấn vào submit sẽ chuyển trang về lại trang login
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form submitted:', formData);
    window.location.href = 'http://localhost:3000/login'; //Chú ý dòng này vì dòng này sẽ chuyển trang về login
  };
  }
  return (

    // Đây là code cái form đki
    <form className="registration-form" onSubmit={handleSubmit}>
        <span className="login-title">Register Form</span>
     
      <div className="form-group">
        <label htmlFor="username">Username:</label>
        <input type="text" name="username" value={formData.username} onChange={handleChange} />
      </div>

      <div className="form-group">
        <label htmlFor="password">Password:</label>
        <input type="password" name="password" value={formData.password} onChange={handleChange} />
      </div>

      <div className="form-group">
        <label htmlFor="confirmPassword">Confirm Password:</label>
        <input type="password" name="confirmPassword" value={formData.confirmPassword} onChange={handleChange} />
      </div>

      <div className="sign-in" onClick={handleSubmit}>
      <button className="submit-button" type="submit">
        Submit
      </button>
      </div>
    </form>
  );
};

export default RegistrationForm;
