import React from 'react';
import Slider from 'react-slick';
import './style.css';
import sliderImage1 from './img/hh1_1632716401.jpg';
import sliderImage2 from './img/hh2_1632716407.jpg';
// Add more image imports as needed

const images = [sliderImage1, sliderImage2];
// Add more images to the array as needed

const MySlider = () => {
  const sliderSettings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
  };

  return (
    <div className="slider-container">
      <div className="slider">
        <Slider {...sliderSettings}>
          {images.map((image, index) => (
            <div key={index}>
              <img src={image} alt={`slide ${index + 1}`} />
            </div>
          ))}
        </Slider>
        <div className="text">WELCOME TO SAFAMO WEBSITE</div>
        <div className="search-container">
          <input
            type="text"
            placeholder="Nhập địa chỉ, Khu vực sinh sống, Zip code"
            id="search-box-input"
          />
          <button id="search-button">Search</button>
        </div>
      </div>
    </div>
  );
};

export default MySlider;
