import React from 'react';
import axios from 'axios';

const FacebookLoginComponent = () => {
  const handleLoginClick = () => {
    // Gửi yêu cầu chuyển hướng đến trang đăng nhập Facebook
    axios.get('https://vi-vn.facebook.com/login/device-based/regular/login')
      .then(response => {
        // Xử lý kết quả nếu cần
        console.log('Chuyển hướng thành công:', response);
        // Chuyển hướng trang
        window.location.href = response.request.responseURL;
      })
      .catch(error => {
        console.error('Lỗi khi chuyển hướng:', error);
      });
  };

  return (
    <div>
      <button onClick={handleLoginClick}>Đăng nhập Facebook</button>
    </div>
  );
};

export default FacebookLoginComponent;
