import React from 'react';
import axios from 'axios';



window.fbAsyncInit = function() {
    window.FB.init({
      appId: '239029875760778',
      autoLogAppEvents: true,
      xfbml: true,
      version: 'v11.0'
    });
  };
const FacebookLoginComponent = () => {
    const handleFacebookLogin = () => {
        window.FB.login(
          response => {
            if (response.authResponse) {
              // Người dùng đã đăng nhập và đã cho phép ứng dụng
              console.log('Đăng nhập thành công:', response);
            } else {
              // Người dùng đã hủy hoặc có lỗi
              console.log('Đăng nhập bị hủy hoặc có lỗi.');
            }
          },
          { scope: 'public_profile,email' } // Phạm vi quyền truy cập
        );
      };
      
      return (
        <div>
          <button onClick={handleFacebookLogin}>Đăng nhập Facebook</button>
        </div>
      );
}
export default FacebookLoginComponent;
