import React from 'react';
import '../css/style.css';
import logo from '../img/logo nguyen trai.png';
import '../bootstrap/dist/css/bootstrap.min.css';

const Navbar = () => {

  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  }
  const handleLogoClick = () => {
    window.location.href = "http://localhost:3000/";
  };

  return (
    <div className="navbar">
      <a href="#About">Trang chủ</a>
      <div className="dropdown">
      <button
          className="btn btn-secondary dropdown-toggle"
          type="button"
          id="dropdownMenuButton1"
          onClick={toggleDropdown}
        >
          Giới thiệu
        </button>
        <div className={`dropdown-menu ${isDropdownOpen ? 'show' : ''}`} aria-labelledby="dropdownMenuButton1">
          <a className="dropdown-item" href="#canbo">Cán bộ trường</a>
          <a className="dropdown-item" href="#giaovien">Giáo viên</a>
          <a className="dropdown-item" href="#khoi10">Khối 10</a>
          <a className="dropdown-item" href="#khoi11">Khối 11</a>
          <a className="dropdown-item" href="#khoi12">Khối 12</a>
        </div>
      </div>

      <button className="btn btn-secondary" type="button">
        Thông báo
      </button>

      <div className="logo" onClick={handleLogoClick}>
        <img src={logo} alt="Logo" />
      </div>

      <div className="dropdown">
        <button className="btn btn-secondary dropdown-toggle" type="button" id="dropdownMenuButton2" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
          Tài nguyên
        </button>
        <div className="dropdown-menu" aria-labelledby="dropdownMenuButton2">
          <a className="dropdown-content" href="#canbo">Cán bộ trường</a>
          <a className="dropdown-content" href="#giaovien">Giáo viên</a>
          <a className="dropdown-content" href="#khoi10">Khối 10</a>
          <a className="dropdown-item" href="#khoi11">Khối 11</a>
          <a className="dropdown-item" href="#khoi12">Khối 12</a>
        </div>
      </div>

      <div className="dropdown">
        <button className="btn btn-secondary dropdown-toggle" type="button" id="dropdownMenuButton3" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
          Thư viện
        </button>
        <div className="dropdown-menu" aria-labelledby="dropdownMenuButton3">
          {/* Dropdown content for Thư viện */}
        </div>
      </div>

      <a href="#About">Thủ tục</a>
    </div>
  );
};

export default Navbar;
